// TodoItem.js
import React, { useContext } from 'react';
import { StateContext } from '../contexts';

export default function TodoItem({ todo }) {
  const { dispatch } = useContext(StateContext);

  if (!todo) {
    return null;
  }

  const { id, title, description, author, dateCreated, complete } = todo;

  const handleComplete = () => {
    dispatch({ type: 'TOGGLE_COMPLETE', payload: id });
  };

  const handleDelete = () => {
    dispatch({ type: 'DELETE_TODO', payload: id });
  };

  return (
    <li>
      <h3>{title}</h3>
      <p>Description: {description}</p>
      <p>Author: {author}</p>
      <p>Created: {new Date(dateCreated).toLocaleString()}</p>
      <label>
        <input type="checkbox" checked={complete} onChange={handleComplete} />
        {complete ? 'Completed' : 'Incomplete'}
      </label>
      <div>
      <button onClick={handleDelete}>Delete</button> {/* Add Delete button */}
    </div>
    </li>
  );
}
